using System;using System.Collections.Generic;namespace ExampleNamespace{    public class Category    {        public int Id { get; set; }        public string Name { get; set; }        public List<Category> SubCategories { get; set; }        public Category(int id, string name)        {            Id = id;            Name = name;            SubCategories = new List<Category>();        }        public void AddSubCategory(Category subCategory)        {            SubCategories.Add(subCategory);        }    }    public class CategoryHelper    {        public List<Category> Categories { get; private set; }        public CategoryHelper()        {            Categories = new List<Category>();        }        public void AddCategory(Category category)        {            Categories.Add(category);        }        public List<Category> GetCategoryHierarchy()        {            return Categories;        }    }    class Program    {        static void Main(string[] args)        {            var rootCategory = new Category(1, "Root");            var subCategory1 = new Category(2, "SubCategory1");            var subCategory2 = new Category(3, "SubCategory2");            rootCategory.AddSubCategory(subCategory1);            rootCategory.AddSubCategory(subCategory2);            var categoryHelper = new CategoryHelper();            categoryHelper.AddCategory(rootCategory);            var hierarchy = categoryHelper.GetCategoryHierarchy();            PrintCategories(hierarchy);        }        static void PrintCategories(List<Category> categories)        {            foreach (var category in categories)            {                Console.WriteLine($"Category: {category.Name}");                PrintSubCategories(category.SubCategories, 1);            }        }        static void PrintSubCategories(List<Category> subCategories, int level)        {            foreach (var subCategory in subCategories)            {                Console.WriteLine(new string(' ', level * 2) + $"SubCategory: {subCategory.Name}");                PrintSubCategories(subCategory.SubCategories, level + 1);            }        }    }}